import React, { useState} from 'react';


import styles from './Toolbar.module.css'
import NavigationItems from '../NavigationItems/NavigationItems';


const Toolbar = (props) => {
    const [showBackground, setShowBackground] = useState(true)

    backgroundVisibilityHandler = (event) => {
        setShowBackground(!showBackground);     
        
    };

    return (
        <header >           
            <div className={styles.navigation}>
                <input type="checkbox" className={styles.navCheckbox} id="navi-toggle" />
                <label htmlFor="navi-toggle" className={styles.navButton} onClick={backgroundVisibilityHandler}>
                    <span className={styles.navIcon}>&nbsp;</span>
                </label>
                <div className={styles.navBackground}>&nbsp;</div>
                <nav className={styles.navNav}>
                    <NavigationItems/>                    
                </nav>
            </div>
        </header>
    )
}

export default Toolbar;
