import React, { useState} from 'react';


import styles from './Toolbar.module.css'
import NavigationItems from '../NavigationItems/NavigationItems';


const Toolbar = (props) => {
    const [showBackground, setShowBackground] = useState(false)

    const backgroundVisibilityHandler = () => {
        setShowBackground({[showBackground]: !showBackground});           
        console.log('heeey')
    };

    let classes = 'styles.navBackground';
    if(showBackground) {        
        classes ='styles.displayNone'
    } 
    else {
        classes = 'styles.navBackground';
    }
    

    return (
        <header >           
            <div className={styles.navigation}>
                <input type="checkbox" className={styles.navCheckbox} id="navi-toggle" />
                <label htmlFor="navi-toggle" className={ styles.navButton} >
                    <span className={styles.navIcon}>&nbsp;</span>
                </label>
                <div className={classes} onClick={(backgroundVisibilityHandler)}>&nbsp;</div>
                <nav className={styles.navNav}>
                    <NavigationItems/>                    
                </nav>
            </div>
        </header>
    )
}

export default Toolbar;
